import './App.css';
import React, { useState } from 'react';
import GoogleLogin from 'react-google-login';


function App() {

  const [loginData,setLoginData] = useState(localStorage.getItem('loginData')? JSON.parse(localStorage.getItem('loginData')):null)


  const handleFailure = (result) => {

  }

  const handleSucces = async (googleData) => {
    const res = await fetch('http://localhost:8080/api/login',{
      method:'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ token: googleData.tokenId})
    })

    const data = await res.json()
    setLoginData(data)
    localStorage.setItem('loginData',JSON.stringify(data))
  }

  return (
    <div className="loginButton ">

      <GoogleLogin
    clientId="545875488565-dsbqcs5les27295vn4aviu56qomp1vel.apps.googleusercontent.com"
    buttonText="Login"
    onSuccess={handleSucces}
    onFailure={handleFailure}
    cookiePolicy={'single_host_origin'}
    ></GoogleLogin>

    </div>
  );
}

export default App;
